import React from 'react';

function About() {
  return (
    <div className="page-content">
      <h1 className="title">About Swapcomm</h1>
      <p>
        Swapcomm aims to transform local trading by serving as a digital channel for communities. 
        This innovative platform merges marketplace dynamics with a crowdfunding approach, 
        fostering social connections, promoting environmental sustainability, 
        and encouraging multinational collaboration.
      </p>
      <p>
        By transcending mere trading, Swapcomm injects communal culture and causes into societal transactions, 
        offering a unique and enriching experience for all users.
      </p>
    </div>
  );
}

export default About;
